mapper.select:
select FRIENDNUM,FROM_FRIEND,TO_FRIEND,IS_FRIEND,REG_DATE,FRIEND_DATE,IDENTIFYDAY
from friendlist
where is_friend = 2 and FROM_FRIEND = #{userId}

select FRIENDNUM,FROM_FRIEND,TO_FRIEND,IS_FRIEND,REG_DATE,FRIEND_DATE,IDENTIFYDAY
from friendlist
where is_friend = 0 and FROM_FRIEND = #{userId}

select nvl(Count(*),0)
from friendlist
where is_friend = 2 and FROM_FRIEND = #{userId}

select username
from member1
where username = #{kwd}
